        // Takes the result and consequences of each guess.
        public void evaluateGuess() 
        {
            // Take input
            UI.getGuess(this);
            // If the input is correct, add the input to knownLetters, render word. 
            if (this.word.Contains(UI.guessLetter)) 
            {
                this.guessResult = true;
                List<int> indexes = Program.allIndexesOf(this.word, UI.guessLetter);
                foreach (int i in indexes) {
                    this.knownLetters[i] = UI.guessLetter;
                }
                // If knownLetters is equal to word.toList<char>(), this.won = true.
                if (new string(this.knownLetters) == word) {
                    this.won = true;
                }
            } else {
                // If the input is incorrect, increase image index, render image, render word. 
                this.guessResult = false;
                this.fails += 1;
                if (this.fails == 6) {
                    // If image index is 6, game.lost = true.
                    this.lost = true;
                }
            }
        }
